import React, { useEffect, useState } from "react";
import { Moon, Sun } from "lucide-react";

export default function ThemeToggle() {
  const [dark, setDark] = useState(false);

  useEffect(() => {
    document.documentElement.classList.toggle("dark", dark);
  }, [dark]);

  return (
    <button
      onClick={() => setDark(!dark)}
      className="fixed top-4 left-4 z-50 bg-gray-200 dark:bg-gray-800 p-2 rounded-full shadow-md hover:scale-105 transition-all duration-300 border dark:border-gray-700"
      title={dark ? "Switch to light mode" : "Switch to dark mode"}
    >
      {dark ? (
        <Sun size={20} className="text-yellow-400 transition-opacity duration-300" />
      ) : (
        <Moon size={20} className="text-gray-700 dark:text-white transition-opacity duration-300" />
      )}
    </button>
  );
}
