import React from "react";
import { Download } from "lucide-react";

export default function DownloadButton({ onClick }) {
  return (
    <button
      onClick={onClick}
      className="fixed bottom-4 left-4 z-50 bg-blue-600 hover:bg-blue-700 text-white px-4 py-2 rounded-full shadow-md flex items-center gap-2 transition-all duration-300 print:hidden"
    >
      <Download size={18} />
      <span className="text-sm font-medium">Download CV</span>
    </button>
  );
}
