import React from "react";
import { education } from "../data/data";

export default function Education() {
  return (
    <section className="mb-12">
      <h2 className="text-2xl font-semibold mb-4 text-gray-900 dark:text-white">Education</h2>
      {education.map((edu, idx) => (
        <div key={idx} className="mb-6 flex items-start gap-4">
          {edu.logo && (
            <img
              src={edu.logo}
              alt={edu.school}
              className="w-10 h-10 object-contain"
            />
          )}
          <div>
            <h3 className="text-lg font-medium text-gray-900 dark:text-white">{edu.school}</h3>
            <p className="text-sm text-gray-600 dark:text-gray-400">
              {edu.degree} | {edu.date} â€“ {edu.location}
            </p>
            <p className="text-sm italic text-gray-500 dark:text-gray-400">
              Thesis: {edu.thesis}
            </p>
            {edu.description && (
              <p className="text-sm text-gray-700 dark:text-gray-300 mt-1">{edu.description}</p>
            )}
          </div>
        </div>
      ))}
    </section>
  );
}
