import React, { useEffect, useState } from "react";
import { Moon, Sun } from "lucide-react";

export default function ThemeToggle() {
  const [dark, setDark] = useState(false);

  useEffect(() => {
    document.documentElement.classList.toggle("dark", dark);
  }, [dark]);

  return (
    <div className="fixed top-4 right-4 z-50 flex items-center gap-2 bg-white/80 dark:bg-gray-800/80 border border-gray-200 dark:border-gray-700 rounded-full px-3 py-1 shadow-md backdrop-blur-sm transition-colors duration-300 print:hidden">
      <button
        onClick={() => setDark(!dark)}
        className="flex items-center gap-2 focus:outline-none text-sm font-medium text-gray-800 dark:text-gray-100"
        title={dark ? "Switch to light mode" : "Switch to dark mode"}
      >
        {dark ? <Sun size={16} className="text-yellow-400" /> : <Moon size={16} className="text-blue-500" />}
        {dark ? "Day mode" : "Night mode"}
      </button>
    </div>
  );
}
